package urlShortener

import org.scalatest.FlatSpec
import scala.Array.canBuildFrom

/** urlValidationTrait tests
 *
 *  Using the FlatSpec style for Fun (argh)
 */
class UrlValidationTraitTest extends FlatSpec with UrlValidationTrait {
  var validUrls = Array("http://foo.com", "https://hootsuite.com")
  // 2048 chars
  validUrls +:= "http://f."

  // i18n

  var invalidUrls = Array("", "f.co", "mailto:d@o.com", "tel:1111111111", "http://foo", "http:/foo.com")
  // 2049 chars
  invalidUrls +:= "http://f."
  // XSS, SQL Injection or other attacks?

  "Valid URLs" should "be Valid" in {
    for (validUrl <- validUrls) assert(isValidUrl(validUrl) == true)
  }

  "Invalid URLs" should "be not valid" in {
    for (invalidUrl <- invalidUrls) assert(isValidUrl(invalidUrl) == false)

  }
}
